function resetAddProductForm() {
  // Đặt lại trạng thái của các nút
  document.getElementById("addCommodityProductButton").disabled = false;
  document.getElementById("addServiceProductButton").disabled = false;
  document.getElementById("addComboProductButton").disabled = false;
  document.querySelectorAll(".btn-edit-product").forEach(function (button) {
    button.disabled = false;
  });
  document.querySelectorAll(".btn-edit-product-service").forEach(function (button) {
  button.disabled = false;
  });
  document.querySelectorAll(".btn-edit-product-combo").forEach(function (button) {
  button.disabled = false;
  });

  // Đặt lại trạng thái của form và các trường nhập liệu
  document.getElementById("addProduct").removeEventListener('click', addCommodityForm);
  document.getElementById("addProduct").removeEventListener('click', addServiceForm);
  document.getElementById("addProduct").removeEventListener('click', addComboForm);
  document.getElementById("addProduct").removeEventListener('click', editCommodityForm);
  document.getElementById("addProduct").removeEventListener('click', editServiceForm);
  document.getElementById("addProduct").removeEventListener('click', editComboForm);
  
  // Đặt lại action cho form
  document.getElementById("addProduct").setAttribute("data-action", "");

  // Đóng form
  document.getElementById("addProduct").style.display = "none";
}

// Hàm để ẩn phần tử addProduct và xóa thuộc tính data-action của #addCommodityProductButton
function hideAddProduct() {
  handleCancelCloseProduct();
  resetAddProductForm();
 
  // Xóa thuộc tính data-action của các phần tử button
document.getElementById("addCommodityProductButton").removeAttribute("data-action");
document.getElementById("addServiceProductButton").removeAttribute("data-action");
document.getElementById("addComboProductButton").removeAttribute("data-action");

// Loại bỏ sự kiện lắng nghe tương ứng với từng button
document.getElementById("addCommodityProductButton").removeEventListener("click", addCommodityButtonClickHandler);
document.getElementById("addServiceProductButton").removeEventListener("click", addServiceButtonClickHandler);
document.getElementById("addComboProductButton").removeEventListener("click", addComboButtonClickHandler);
}

// Hàm để ẩn phần tử addProduct và xóa thuộc tính data-action của #addCommodityProductButton
function hideEditProduct() {
  handleCancelCloseProduct();
  resetAddProductForm();
 
  // Xóa thuộc tính data-action của các nút "Edit Product"
  document.querySelectorAll(".btn-edit-product").forEach(function (button) {
      button.removeAttribute("data-action");
  });
  document.querySelectorAll(".btn-edit-product-service").forEach(function (button) {
    button.removeAttribute("data-action");
});
document.querySelectorAll(".btn-edit-product-combo").forEach(function (button) {
  button.removeAttribute("data-action");
});

// Loại bỏ sự kiện lắng nghe tương ứng với từng button
document.querySelectorAll(".btn-edit-product").forEach(function (button) {
  button.removeEventListener("click", editCommodityButtonClickHandler);
});
document.querySelectorAll(".btn-edit-product-service").forEach(function (button) {
button.removeEventListener("click", editServiceButtonClickHandler);
});
document.querySelectorAll(".btn-edit-product-combo").forEach(function (button) {
button.removeEventListener("click", editComboButtonClickHandler);
});
}

